import React, { Component } from "react";
import "./App.css";
import Button from "../Button/Button";
import Display from "../Display/Display";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      buttons: {
        equals: "=",
        zero: "0",
        one: "1",
        two: "2",
        three: "3",
        four: "4",
        five: "5",
        six: "6",
        seven: "7",
        eight: "8",
        nine: "9",
        add: "+",
        subtract: "-",
        multiply: "*",
        divide: "/",
        decimal: ".",
        clear: "C",
      },
      inputValue: null,
      result: 0,
    };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = (e) => {
    console.log(e.target);

    // Determine which button has been clicked and handle it accordingly
    switch (e.target.id) {
      case x:
        // code
        break;
      default:
        console.warn("That button does not exist!");
    }
  };

  render() {
    return (
      <div className="App">
        <Display />
        <button onClick={this.handleClick}>Test</button>
        {Object.entries(this.state.buttons).map(([key, value]) => {
          return (
            <Button key={key} id={key} value={value} event={this.handleClick} />
          );
        })}
      </div>
    );
  }
}

export default App;
